package com.social.innerPeace.board.comment.controller;

import com.social.innerPeace.board.comment.service.BoardCommentService;
import com.social.innerPeace.dto.CommentDTO;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.*;

import java.time.LocalDate;
import java.util.ArrayList;
import java.util.List;
import java.util.stream.IntStream;

@Slf4j
@Controller
@RequiredArgsConstructor
@RequestMapping("/board/comment")
public class BoardCommentController {
    private final BoardCommentService boardCommentService;

    @PostMapping("/write")
    public ResponseEntity<Object> save(@RequestBody CommentDTO commentDTO, @SessionAttribute(name = "loginedHealer") String healer) {
        System.out.println("commentDTO = " + commentDTO);
        if (healer==null) {
            return null;
        }
        commentDTO.setNickName(healer);
        Long saveResult = boardCommentService.save(commentDTO);
        if (saveResult != null) {
            return ResponseEntity.ok(saveResult);
        } else {
            return new ResponseEntity<>("해당 게시글이 존재하지 않습니다",HttpStatus.NOT_FOUND);
        }
    }

    @PostMapping("/scroll")
    public ResponseEntity<Object> scroll(@RequestParam("post_no") int post_no,@RequestParam("comment_no") int comment_no) {
        String profileImgUrl = "data:image/webp;base64,";
        log.info("comment scroll post_no : {} comment_no : {}",post_no,comment_no);
        List<String> dtoList = new ArrayList<>();
        IntStream.rangeClosed(comment_no+1,comment_no+10).forEach(i->{
            String htmlContent = "<section class=\"w100 pad10 row\">"
                    + "<article class=\"w5\">"
                    + "<img class=\"w30p hauto\" src=\"" + profileImgUrl + "\" alt=\"프로필\">"
                    + "</article>"
                    + "<article class=\"w80 hauto ml20 mr20 col\">"
                    + "<span>"
                    + "<a href=\"/user/userpage/" + "123" + "\">"
                    + "<span class=\"text_hover\">" + "123" + "</span>"
                    + "</a>"
                    + "  " + "ㅎㅇㅎㅇ"
                    + "</span>"
                    + "<article class=\"mt10 font_s15 row_cb\">"
                    + "<span class=\"regday\">" + LocalDate.now() + "</span>"
                    + "</article>"
                    + "</article>"
                    + "<article class=\"w10 hauto\">"
                    // 여기에 버튼 추가
                    + "<input class=\"bor_none bg_e0 text_hover font_s20\" type=\"button\" value=\"···\" "
                    + "onclick=\"comment_menu('" + i + "','" + "ㅎㅇㅎㅇ".replace("'", "\\'") + "','" + "123" + "')\">"
                    + "</article>"
                    + "</section>";
            dtoList.add(htmlContent);
        });


        return ResponseEntity.status(HttpStatus.OK).body(dtoList);
    }
}
